const input = JSON.parse($input);

// Extract search parameters from the input
const args = {
  limit: input.limit || 10,
  page: input.page || 1
};

if (input.category) args.category = input.category;
if (input.destination) args.destination = input.destination;
if (input.startDate) args.startDate = input.startDate;
if (input.endDate) args.endDate = input.endDate;

// Call Victoury MCP server
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'http://victoury-mcp:3000/tools/list_products',
  body: {
    arguments: args,
    credentials: {
      apiUrl: $vars.victoury_api_url || 'https://api.victoury.com/v2',
      tenant: $vars.victoury_tenant || 'your-tenant',
      sessionId: $vars.victoury_session || 'your-session'
    }
  }
});

if (response.success && response.result?.data) {
  const products = response.result.data;
  return {
    json: {
      found: products.length,
      products: products.map(p => ({
        id: p.id,
        name: p.name,
        category: p.category || 'tour',
        destination: p.destination,
        price: p.price,
        description: p.description?.substring(0, 100) + '...'
      }))
    }
  };
} else {
  return {
    json: {
      error: 'No products found',
      details: response.error
    }
  };
}

