if (input.phone) args.phone = input.phone;
if (input.query) args.query = input.query;
args.limit = input.limit || 10;

// Call Victoury MCP server
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'http://victoury-mcp:3000/tools/search_customers',
  body: {
    arguments: args,
    credentials: {
      apiUrl: $vars.victoury_api_url || 'https://api.victoury.com/v2',
      tenant: $vars.victoury_tenant || 'your-tenant',
      sessionId: $vars.victoury_session || 'your-session'
    }
  }
});

if (response.success && response.result?.data) {
  const customers = response.result.data;
  return {
    json: {
      found: customers.length,
      customers: customers.map(c => ({
        id: c.id,
        name: `${c.firstName} ${c.lastName}`,
        email: c.email,
        phone: c.phone
      }))
    }
  };
} else {
  return {
    json: {
      error: 'No customers found',
      details: response.error
    }
  };
}

const input = JSON.parse($input);

if (!input.productId) {
  return { json: { error: 'Product ID is required' } };
}

const args = {
  productId: input.productId,
  startDate: input.startDate || new Date().toISOString().split('T')[0],
  endDate: input.endDate || input.startDate || new Date().toISOString().split('T')[0],
  participants: input.participants || 1
};

// Call Victoury MCP server
const response = await this.helpers.httpRequest({
  method: 'POST',
  url: 'http://victoury-mcp:3000/tools/list_availability',
  body: {
    arguments: args,
    credentials: {
      apiUrl: $vars.victoury_api_url || 'https://api.victoury.com/v2',
      tenant: $vars.victoury_tenant || 'your-tenant',
      sessionId: $vars.victoury_session || 'your-session'
    }
  }
});

if (response.success && response.result?.data) {
  return {
    json: {
      productId: args.productId,
      dates: `${args.startDate} to ${args.endDate}`,
      participants: args.participants,
      availability: response.result.data
    }
  };
} else {
  return {
    json: {
      error: 'Could not check availability',
      details: response.error
    }
  };
}
